mapper.select:
select 

from tb_button 
where 1=1 

and level=#{level} 


and super_id=#{super_id} 

order by sort

select   count(1)   from tb_button 
where 1=1 

and level=#{level} 


and super_id=#{super_id}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tb_button (id, name, type, 
  value, super_id, add_time,sort,level
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{value,jdbcType=VARCHAR}, #{super_id,jdbcType=VARCHAR}, #{add_time,jdbcType=VARCHAR}, #{sort}, #{level}
  )

mapper.update:
update tb_button
set  sort =  #{sort}
where id = #{id,jdbcType=VARCHAR}
